import React from 'react'
import tv from '../tv.png';
import tvvideo from '../video-tv-in-0819.mp4';
import ReactPlayer from 'react-player'
import mobileplayer from '../mobile-0819.jpg';

function Services() {
  return (
    <>
    {/* first section */}
        <div className='flex bg-black py-5 my-2 w-full justify-evenly lg:px-20 px-2 lg:flex-row flex-col text-white relative'>
            <div className='lg:text-white flex justify-center flex-col lg:text-left lg:px-20 px-1 text-center text-white lg:w-1/2'>
                <h1 className='lg:text-5xl text-3xl font-bold'>Enjoy on your TV.</h1><br />
                <p className='lg:text-2xl text-xl font-normal'>Watch on smart TVs, PlayStation, Xbox, Chromecast, Apple TV, Blu-ray players and more.</p>
            </div>
            <div className='lg:w-1/3'>
                <img src={tv} alt="" className="service_image" />
            <ReactPlayer
             url={tvvideo} 
             width="285px"
             height="130%"

             loop={true}
             playing={true}
             autoPlay={true}
             muted={true}
            
             
             className='tv_video'/>
            </div>
            
           

            
        </div>

        {/* second section */}
        <div className='flex bg-black py-2 my-2 w-full justify-eve lg:px-20 px-2 lg:flex-row flex-col text-white relative'>
          <div className='w-1/2 flex items-center justify-center'>
              <img src={mobileplayer} alt="" className='' />
              
          </div>
          <div className='w-1/2 flex items-center flex-col justify-center'>
              <h1 className='lg:text-5xl text-3xl font-bold'>Download your shows to watch offline.</h1>
              <p className='lg:text-2xl text-xl font-normal'>Save your favourites easily and always have something to watch.
</p>
          </div>

        </div>
          
    </>
  )
}

export default Services
